import subprocess
import tempfile

from fandango.evolution.algorithm import FANDANGO
from fandango.language.parse import parse_file


def is_syntactically_valid_tar(tree: str):
    with tempfile.NamedTemporaryFile(suffix=".tar") as outfile:
        outfile.write(tree.encode())
        outfile.flush()
        cmd = ["tar", "tf", outfile.name]
        process = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        (stdout, stderr) = process.communicate(timeout=2)
        exit_code = process.wait()

        output_msg = stdout.decode("utf-8").strip()
        err_msg = stderr.decode("utf-8").strip()

        print(f"Output: {output_msg}")

        if exit_code == 0:
            return True
        else:
            print(f"Error: {err_msg}")
            return False


def evaluate_tar():
    grammar, constraints = parse_file("tar_evaluation/tar.fan")

    fandango = FANDANGO(grammar, constraints, verbose=True)
    fandango.evolve()

    valid = []
    for sol in fandango.solution:
        if is_syntactically_valid_tar(str(sol)):
            print(str(sol))
            valid.append(str(sol))

    print(f"Valid solutions: {len(valid)}")


def produce_single_valid_tar():
    grammar, constraints = parse_file("tar.fan")

    valid = []

    while len(valid) < 1:
        fandango = FANDANGO(
            grammar, constraints, verbose=True, desired_solutions=1, population_size=30
        )
        fandango.evolve()

        for sol in fandango.solution:
            print(str(sol))
            if is_syntactically_valid_tar(str(sol)):
                valid.append(str(sol))

    print(f"Valid solution: {valid[0].encode()}")


